insert  into `ana`(`id`,`user_id`,`ana_type_id`,`ana_title`,`ana_content`,`comment_num`,`prize_num`,`create_date`) values (300,2,4,'心灵捕手','我每天到你住处来接你。我们出去花天酒地，玩得很开心。但你可知我一天中最好的时刻是什么吗？大概只有十秒：从停车到走到你住所门口的这段时间。因为每当我敲门，都希望你不在屋子里面。没有一句\"再见\"或是\"明天见\"，什么都不用说，你就是这样离开了。我懂得不多，但这些我很清楚。',0,0,'2019-09-21 10:54:18'),(301,2,1,'世俗','女孩想看世俗，在身上挂了个牌子写：我是艾滋病人，抱抱我好吗？结果她看到行人绕着她走，对她指指点点。当她暗笑那些人时，跑来个男孩抱了她，她很诧异，他告诉她：别怕，我也是艾滋病患者。她听到后像疯一样推开他。 ​​​​',0,0,'2019-09-23 10:58:01');
